import React from "react";
import "./App.css";
import Buttons from "./components/buttons";
import Display from "./components/display";

class App extends React.Component {
  state = {
    input: "0",
    inputArr: [],
    buttonId: [
      "clear",
      "divide",
      "multiply",
      "nine",
      "eight",
      "seven",
      "subtract",
      "six",
      "five",
      "four",
      "add",
      "three",
      "two",
      "one",
      "equals",
      "zero",
      "decimal",
    ],
    buttonValue: [
      "AC",
      " / ",
      " x ",
      "9",
      "8",
      "7",
      " - ",
      "6",
      "5",
      "4",
      " + ",
      "3",
      "2",
      "1",
      "=",
      "0",
      ".",
    ],
  };
  onButtonClick = (e) => {
    const regex = "/d{1,}[.]{1}d{1,}/g";
    if (this.state.input === "0") {
      this.setState({ input: e.target.value });
    } else if (e.target.value === "." && this.state.input.indexOf(".") >= 0) {
      return;
    } else {
      this.setState({ input: this.state.input + e.target.value });
    }

    if (e.target.value === "=") {
      this.setState({ input: String(eval(this.state.input)) });
    } else if (e.target.value === " x ") {
      this.setState({ input: this.state.input + " * " });
    } else if (e.target.value === "AC") {
      this.setState({ input: "0" });
    }
  };
  render() {
    const input = this.state.input;
    const buttonId = this.state.buttonId.slice();
    const buttonValue = this.state.buttonValue.slice();

    return (
      <div className="App" id="root-container">
        <div id="calculator">
          <Display input={input} />
          <Buttons
            value={buttonValue}
            id={buttonId}
            onButtonClick={this.onButtonClick}
          />
        </div>
      </div>
    );
  }
}

export default App;
