import React from "react";
import "./Session Length.css";

class SessionLength extends React.Component {
  state = { SessionLength: 5 };

  onSessionIncrement = () => {
    this.setState({ SessionLength: this.state.SessionLength + 1 });
  };

  onSessionDecrement = () => {
    this.setState({ SessionLength: this.state.SessionLength - 1 });
  };

  render() {
    return (
      <div>
        <h3 id="session-label">Session Length</h3>
        <div id="session-buttons">
          <button id="session-decrement" onClick={this.onSessionDecrement}>
            <i class="fas fa-arrow-down"></i>
          </button>
          <h4 id="sesson-length">{this.state.SessionLength}</h4>

          <button id="session-increment" onClick={this.onSessionIncrement}>
            <i class="fas fa-arrow-up"></i>
          </button>
        </div>
      </div>
    );
  }
}

export default SessionLength;
